export async function getAllUsers(): Promise<User[]> {
    const usersCol = collection(db, "users");
    const usersSnapshot = await getDocs(usersCol);
    return usersSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as User));
}

export async function getUserData(userId: string): Promise<User | null> {
    if (!userId) return null;
    const userDoc = await getDoc(doc(db, "users", userId));
    if (userDoc.exists()) {
        const data = userDoc.data();
        
        let memberSince: string;
        if (data.memberSince && typeof data.memberSince.toDate === 'function') {
            memberSince = (data.memberSince as Timestamp).toDate().toISOString();
        } else if (typeof data.memberSince === 'string') {
            memberSince = data.memberSince;
        } else {
            memberSince = new Date().toISOString();
        }

        return {
            id: userId,
            name: data.name || 'Anonymous',
            email: data.email,
            avatarUrl: data.photoURL || `https://picsum.photos/seed/${userId}/100/100`,
            location: data.location || 'Kenya',
            memberSince: memberSince,
            rating: data.rating,
            reviews: data.reviews,
        } as User;
    }
    return null;
}

export async function addUserData(user: FirebaseUser, additionalData: { name?: string | null }) {
    const userRef = doc(db, 'users', user.uid);
    const docSnap = await getDoc(userRef);

    if (!docSnap.exists()) {
        await setDoc(userRef, {
            email: user.email,
            name: additionalData.name || user.displayName || 'Anonymous',
            photoURL: user.photoURL || `https://picsum.photos/seed/${user.uid}/100/100`,
            memberSince: serverTimestamp(),
            status: 'Active',
            type: 'Client'
        });
    }
}

